import React, {useState} from 'react'
import socket from '../../context/socket.js'
import classes from './ColorPanel.module.css'

const ColorPanel = (props) => {



  const [color, setColor] = useState("#000000")

  const [colorOptions, setColorOptions] = useState(true)

  const handleChange = (e) => {
    setColor(e.target.value)
    props.handleColor(e.target.value)
  }

  useEffect(() => {
    socket.on('nocolor', () => {
      setColor("#000000")
      props.handleColor("#000000")
      setColorOptions(false)
    })
    socket.on('nocolorclear', () => {
      setColorOptions(true)
    })
    return () => {
      socket.off('nocolor')
      socket.off('nocolorclear')
    }

  })



  return(
    <div className={classes.colorHolder}>


        <form onChange={handleChange} className={classes.colorForm}>
          <label className={classes.container}>
            <input type="radio" name="coloroption" value="#000000"/>
            <span style={{backgroundColor:"#000000"}} className={classes.checkmark}></span>
          </label>


          {colorOptions && <label className={classes.container}>
            <input type="radio" name="coloroption" value="#ffff00"/>
            <span style={{backgroundColor:"#ffff00"}} className={classes.checkmark}></span>
          </label>}

          {colorOptions && <label className={classes.container}>
            <input type="radio" name="coloroption" value="#ff00ff"/>
            <span style={{backgroundColor:"#ff00ff"}} className={classes.checkmark}></span>
          </label>}
          {colorOptions && <label className={classes.container}>
            <input type="radio" name="coloroption" value="#00ffff"/>
            <span style={{backgroundColor:"#00ffff"}} className={classes.checkmark}></span>
          </label>}


          {colorOptions && <label className={classes.container}>
            <input type="radio" name="coloroption" value="#ff0000"/>
            <span style={{backgroundColor:"#ff0000"}} className={classes.checkmark}></span>
          </label>}
          {colorOptions && <label className={classes.container}>
            <input type="radio" name="coloroption" value="#00ff00"/>
            <span  style={{backgroundColor:"#00ff00"}}className={classes.checkmark}></span>
          </label>}
          {colorOptions && <label className={classes.container}>
            <input type="radio" name="coloroption" value="#0000ff"/>
            <span style={{backgroundColor:"#0000ff"}} className={classes.checkmark}></span>
          </label>}
        </form>



    </div>

  )
}
export default ColorPanel
