import React, {useState} from 'react'
import classes from './SizePanel.module.css'

const SizePanel = (props) => {


  const [sizeOptions, setSizeOptions] = useState(true)

  const handleChange = (e) => props.handleSize(e.target.value)

  return(
    <div className={classes.colorHolder}>
      <form onChange={handleChange} className={classes.sizeForm}>

      {sizeOptions && <label className={classes.container}>
    <input type="radio" name="coloroption" value={5}/>
    <span style={{backgroundColor:"#aaaaaa", width:"9px", height:"9px", top:"8px", left:"8px"}} className={classes.checkmark}></span>
  </label>}
  {sizeOptions && <label className={classes.container}>
    <input type="radio" name="coloroption" value={12}/>
    <span style={{backgroundColor:"#aaaaaa", width:"15px", height:"15px", top:"5px", left:"5px"}} className={classes.checkmark}></span>
  </label>}
  <label className={classes.container}>
    <input type="radio" name="coloroption" value={18}/>
    <span  style={{backgroundColor:"#aaaaaa", width:"25px", height:"25px"}}className={classes.checkmark}></span>
  </label>

  </form>

    </div>
  )
}
export default SizePanel
