import React, { useState } from 'react'
import classes from './MessageInput.module.css'
import socket from '../../context/socket.js'

const MessageInput = ({isDrawing}) => {

  const [message, setMessage] = useState('')


  const handleChange = (e) => setMessage(e.target.value)
  const handleSubmit = (e) => {
    e.preventDefault()
    socket.emit('guess', {guess:message})
    setMessage('')
  }

  return(
    <form className={classes.guessform} onSubmit={handleSubmit}>
      <input disabled={isDrawing} className={classes.guessInput} type='text' value={message} onChange={handleChange}></input>
    </form>
  )
}

export default MessageInput
