import { useEffect } from 'react'
import classes from './Preview.module.css'
import socket from '../../context/socket.js'

const Preview = ({round, redDrawer, blueDrawer}) => {

  useEffect(() => {
    setTimeout(() => {socket.emit('getRoundData')}, 3000)
  }, [])


  return (
    <div className={classes.previewContainer}>
      <h1>{round}</h1>
      <h2 style={{textDecorationLine:'underline'}}>Drawers</h2>
      <div className={classes.matchupContainer}>
        <h2 style={{color:"#ff3b2d"}}>{redDrawer}</h2>
        <h3>vs.</h3>
        <h2 style={{color:"#2df1ff"}}>{blueDrawer}</h2>

      </div>
    </div>
  )
}
export default Preview
