import {useState, useEffect} from 'react'
import socket from '../../context/socket.js'

import classes from './AttackCard.module.css'

const AttackCard = ({type, color, timestamp, maxTime}) => {

  const [secs, setSecs] = useState(0)

  useEffect(() => {
    let int = null
    int = setInterval(() => {
      setSecs(maxTime-(Math.round((Date.now() - timestamp)/1000)))
      if (secs <= 0){
        filterOut(timestamp)
      }
    }, 1000)
    return () => {
      clearInterval(int)
    }

  }, [])

  return (
    <div style={{backgroundColor:color}} className={classes.cardContainer}>
      <h1>{type.title}</h1>
      <h2>{type.explainer}</h2>
      <div className={classes.timerspot}>
        {secs}
      </div>
      <h3>{type.flavor}</h3>

    </div>
  )
}

export default AttackCard
