import {useEffect, useState } from 'react'
const Timer = ({maxTime, startTime}) => {



  const [secs, setSecs] = useState(0)




  useEffect(() => {
    let int = null
    if (startTime == 0){
      return
    }
    int = setInterval(() => {setSecs(maxTime-(Math.floor((Date.now() - startTime)/1000)))}, 1000)
    return function cleanup(){
      clearInterval(int)

    }

  }, [startTime])

  return (
    <div>
      <div>{secs}</div>
    </div>
  )

}

export default Timer
