import React, {useState, useEffect} from 'react'
import socket from '../../context/socket.js'
import classes from './ColorPanel.module.css'

const ColorPanel = (props) => {



  const [color, setColor] = useState("#000000")

  const [colorOptions, setColorOptions] = useState(true)

  const handleChange = (e) => {
    setColor(e.target.value)
    props.handleColor(e.target.value)
  }

  useEffect(() => {
    socket.on('nocolor', () => {
      setColor("#000000")
      props.handleColor("#000000")
      setColorOptions(false)
    })
    socket.on('nocolorclear', () => {
      setColorOptions(true)
    })
    return () => {
      socket.off('nocolor')
      socket.off('nocolorclear')
    }

  })



  return(
    <div className={classes.colorHolder}>


        <form onChange={handleChange} className={classes.colorForm}>
          <label className={classes.container}>
            <input type="radio" name="coloroption" value="#000000"/>
            <span style={{backgroundColor:"#000000"}} className={classes.checkmark}></span>
          </label>


          {colorOptions && <label className={classes.container}>
            <input type="radio" name="coloroption" value="#ff0000"/>
            <span style={{backgroundColor:"#ff0000"}} className={classes.checkmark}></span>
          </label>}

          {colorOptions && <label className={classes.container}>
            <input type="radio" name="coloroption" value="#ff7900"/>
            <span style={{backgroundColor:"#ff7900"}} className={classes.checkmark}></span>
          </label>}
          {colorOptions && <label className={classes.container}>
            <input type="radio" name="coloroption" value="#ffe800"/>
            <span style={{backgroundColor:"#ffe800"}} className={classes.checkmark}></span>
          </label>}


          {colorOptions && <label className={classes.container}>
            <input type="radio" name="coloroption" value="#30cb00"/>
            <span style={{backgroundColor:"#30cb00"}} className={classes.checkmark}></span>
          </label>}
          {colorOptions && <label className={classes.container}>
            <input type="radio" name="coloroption" value="#0059ff"/>
            <span  style={{backgroundColor:"#0059ff"}}className={classes.checkmark}></span>
          </label>}
          {colorOptions && <label className={classes.container}>
            <input type="radio" name="coloroption" value="#ffooe3"/>
            <span style={{backgroundColor:"#ffooe3"}} className={classes.checkmark}></span>
          </label>}
          {colorOptions && <label className={classes.container}>
            <input type="radio" name="coloroption" value="#a100d4"/>
            <span style={{backgroundColor:"#a100d4"}} className={classes.checkmark}></span>
          </label>}
          {colorOptions && <label className={classes.container}>
            <input type="radio" name="coloroption" value="#a1705e"/>
            <span style={{backgroundColor:"#a1705e"}} className={classes.checkmark}></span>
          </label>}

        </form>



    </div>

  )
}
export default ColorPanel
